import * as R from 'ramda'

import goods from 'api/mockGoods'
import categories from 'api/mockCategories'

export const fetchGoods = async () => {
    //return await superagent.get('/api')
    return new Promise((resolve, reject) => {
        resolve(goods)
        //reject('error')
    })
}

export const loadMoreGoods = async ({offset}) => {
    //fetch.get(`http://google.com/api/goods?offset=${offset}`)
    return new Promise((resolve, reject) => {
        resolve(goods)
        //reject('error')
    })
}

export const fetchGoodById = async (id) => {
    
    return new Promise((resolve, reject) => {
        const good = R.find(R.propEq('id', id), goods)
        resolve(good)
        //reject('error')
    })
}

export const fetchCategories = async () => {
    //return await superagent.get('/api')
    return new Promise((resolve, reject) => {
        resolve(categories)
        //reject('error')
    })
}